#!/usr/bin/env python3# -*- coding: utf-8 -*-"""간소화된 PDF 추출기 테스트"""import osimport sysfrom pathlib import Path# pdf_extractor 모듈 임포트from pdf_extractor import PDFExtractordef test_simple_extraction():    """간소화된 추출 기능 테스트"""    print("🧪 간소화된 PDF 추출기 테스트")        # 파일 경로 설정    pdf_path = "test/example.pdf"    output_dir = "output"        if not os.path.exists(pdf_path):        print(f"❌ PDF 파일을 찾을 수 없습니다: {pdf_path}")        return False        if not os.path.exists(output_dir):        os.makedirs(output_dir)        print(f"📁 출력 폴더 생성: {output_dir}")        # PDFExtractor 인스턴스 생성    extractor = PDFExtractor()        print(f"📄 처리할 파일: {pdf_path}")    print(f"📂 출력 폴더: {output_dir}")        try:        # 새로운 extract_tables 메서드 테스트        success, result = extractor.extract_tables(            pdf_path,             output_dir,             method="pdfplumber"        )                if success:            print(f"✅ 추출 성공: {result}")                        # 결과 파일 확인            output_files = list(Path(output_dir).glob("*.xlsx"))            if output_files:                print(f"📊 생성된 파일:")                for file in output_files:                    file_size = file.stat().st_size                    print(f"  - {file.name} ({file_size} bytes)")                        return True        else:            print(f"❌ 추출 실패: {result}")            return False                except Exception as e:        print(f"💥 오류 발생: {str(e)}")        import traceback        print(traceback.format_exc())        return Falseif __name__ == "__main__":    success = test_simple_extraction()    if success:        print("\n🎉 테스트 성공!")    else:        print("\n😞 테스트 실패!")        sys.exit(1)